import React from "react";
import { withRouter } from "react-router-dom";
import deleteIcon from '../../assets/icons/delete-black-18dp.svg'
import editIcon from '../../assets/icons/create-black-18dp.svg'
import './home-page.scss';
import { Link } from 'react-router-dom';
import EmployeeService from '../../services/employee-service.js';
import PayrollForm from "../payroll-form/payroll-form";
import property from "./global-variables";
import { render } from "@testing-library/react";
import updateed from "./update";

var employeeService = new EmployeeService();
const Display = (props) => {


    const remove = (id) => {
        console.log("remove")

    }

    var passData;
    const hello=(id)=>{

        //alert("123")
        employeeService.getEmployeeById(id).then(obj => {

                    passData=obj.data;
                    alert(passData)


                })
                    .catch(err => {
                        console.log("err while get" + err);

                    })
    }
    // var passData;

    //  const update = (id) => {

    //     employeeService.getEmployeeById(id).then(obj => {

    //         passData=obj;


    //     })
    //         .catch(err => {
    //             console.log("err while get" + err);

    //         })
    // }

    return (

        <table id="display" className="table">
            <tbody>
                <tr key={-1}>
                    <th></th>
                    <th>Name</th>
                    <th>Gender</th>
                    <th>Department</th>
                    <th>Salary</th>
                    <th>Start Date</th>
                    <th>Actions</th>"

                </tr>
                {
                    props.employeeArray && props.employeeArray.map((element, ind) => (
                        <tr key={ind}>
                            <td><img className="profile" src={element.profileUrl} alt="" /></td>
                            <td>{element.name}</td>
                            <td>{element.gender}</td>
                            <td><div className="depts">
                                {element.departMentValue && element.departMentValue.map(dept => (
                                    <div className="dept-label">{dept}</div>
                                ))}
                            </div></td>
                            <td>{element.salary}</td>
                            <td>{element.startDate}</td>
                            <td>
                                <img onClick={()=> remove(element.id)} src ={deleteIcon} alt="delete"/>
                                <Link  onClick={hello(element.id)} to={{
                                pathname:'/payroll-form', 
                                state:{
                                    isUpdate:true,
                                    empToUpdate:{passData},
                                    empId:element.id

                                }}}>

                                <img src ={editIcon} alt="edit"/>

                                </Link>
                            </td>

                        </tr>
                    ))
                }
            </tbody>
        </table>


    )
}

export default withRouter(Display)
