import React, {useState, useEffect} from 'react'
import { Link } from 'react-router-dom';
import EmployeeService from '../../services/employee-service.js';
import addIcon from '../../assets/icons/add-24px.svg'
import './home-page.scss';
import PageHeader from '../page-header/page-header';
import editIcon from '../../assets/icons/create-black-18dp.svg'
import deleteIcon from '../../assets/icons/delete-black-18dp.svg'
import 'typeface-roboto';

var employeeService = new EmployeeService();


const HomePage = () => {

    const [employees, setEmployees] = useState([])

    useEffect(() => {

        getAllEmployees();
    }, [])

    const getAllEmployees = () => {
        employeeService.getAllEmployee().then((response) => {
            console.log(response.data.data);
            setEmployees(response.data.data);

        }).catch(error =>{
            console.log(error);
        })
    }

    const deleteEmployee = (employeeId) => {
        employeeService.deleteEmployee(employeeId).then((response) =>{
         getAllEmployees();

        }).catch(error =>{
            console.log(error);
        })

     }



        return (

            <div >
                <PageHeader />

                <div className="main-content">
                    <div className="header-content">
                        <div className="emp-detail-text">
                            Employee Details <div className="emp-count"></div>
                        </div>
                        <div className="row center button box">
                            {/* <div className="search-box" onClick={this.openSearch}>
                                <input className={"input"+(this.state.searchExpand && 'input-expand')}
                                 onChange={this.search} type="text" placeholder=""/>
                                 <img className="search-icon" src={searchIcon} alt=""/>
                            </div> */}
                            <Link to="payroll-form" className="add-button flex-row-center">
                                <img src={addIcon} alt="" />Add User
                            </Link>
                        </div>
                    </div>
                    <div className="table-main" >
                        <table id="display" className="table">
                            <tbody>
                                <tr key={-1}>
                                    <th></th>
                                    <th>Name</th>
                                    <th>Gender</th>
                                    <th>Department</th>
                                    <th>Salary</th>
                                    <th>Start Date</th>
                                    <th>Actions</th>"

                                </tr>
                                {
                                    employees && employees.map((employee, ind) => (
                                        <tr key={ind}>
                                            <td><img className="profile" src={employee.profileUrl} alt="" /></td>
                                            <td>{employee.name}</td>
                                            <td>{employee.gender}</td>
                                            <td><div className="depts">
                                                {employee.departMentValue && employee.departMentValue.map(dept => (
                                                    <div className="dept-label">{dept}</div>
                                                ))}
                                            </div></td>
                                            <td> {'\u20B9'}{employee.salary}</td>
                                            <td>{employee.startDate}</td>
                                            <td>
                                                {/* <img onClick={()=> remove(employee.id)} src ={deleteIcon} alt="delete"/> */}
                                                <Link    className="btn btn-info" 
                                                        to={`/edit-employee/${employee.id}`} >

                                                <img src ={editIcon} alt="edit"/>

                                                </Link>
                                                <img onClick={() => deleteEmployee(employee.id)} src ={deleteIcon} alt="delete"/>

                                                {/* <button className = "btn btn-danger" onClick = {() => deleteEmployee(employee.id)} */}
                                                 {/* style = {{marginLeft:"10px"}}> Delete</button> */}

                                            </td>

                                        </tr>
                                    ))
                                }
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        )

    }



export default HomePage
