import React,{useState,usEffect} from 'react';
import './payroll-form.scss';
import logo from '../../assets/images/logo.png'
import { useParams,Link,withRouter } from 'react-router-dom';
import EmployeeService from '../../services/employee-service.js';
import DisplayMessage from '../displayMessage/display-message';
import PageHeader from '../page-header/page-header';
import property from '../home-page/global-variables';
import {useLocation} from "react-router-dom";


var profile1="profile-images/Ellipse -1.png"
var profile2="profile-images/Ellipse -2.png"
var profile3="profile-images/Ellipse -3.png"
var profile4="profile-images/Ellipse -4.png"


var employeeService = new EmployeeService();
const PayrollForm=(props)=>{
    let data = useLocation();
    console.log(data.state);

    let initialValue={

        name:'',
        profileArray:[
            {url:'profile-images/Ellipse -3.png'},
            {url:'.profile-images/Ellipse -2.png'},
            {url:'profile-images/Ellipse -1.png'},
            {url:'profile-images/Ellipse -8.png'},

        ],
        allDepartment:[
            'HR','Sales','Finance','Engineer','Others'
        ],
        departMentValue:[],
        gender:'',
        salary:'',
        day:'1',
        month:'Jan',
        year:'2020',
        startDate:'',
        notes:'',
        id:'',
        profileUrl:'',
        isUpdate:true,
        error:{

            department:'',
            name:'',
            gender:'',
            salary:'',
            profileUrl:'',
            startDate:''
        }

    }

    if('state' in data)
    {

    }
     if( data.state.isUpdate==true)
    {
        initialValue.name=data.state.empToUpdate.name;
        initialValue.gender=data.state.empToUpdate.gender;
        initialValue.salary=data.state.empToUpdate.salary;
        initialValue.startDate=data.state.empToUpdate.startDate;
        initialValue.notes=data.state.empToUpdate.notes;
        initialValue.profileUrl=data.state.empToUpdate.profileUrl
        initialValue.departMentValue=data.state.empToUpdate.departMentValue;

    }


    const [formValue,setForm]=useState(initialValue);
    const [displayMessage,setDisplayeMessage]=useState("");



    const changeValue=(event)=>{
        setForm({...formValue,[event.target.name]:event.target.value})
    }

    const onCheckChange=(name)=>
    {
        let index=formValue.departMentValue.indexOf(name);
        let checkArray=[...formValue.departMentValue]
        if(index>-1)
            checkArray.splice(index,1)
        else
            checkArray.push(name);

        setForm({...formValue,departMentValue:checkArray});
    }

    const getChecked=(name)=>{
        return formValue.departMentValue && formValue.departMentValue.includes(name);
    }

    const validData=async()=>{
        let isError=false;
        let error={
            department:'',
            name:'',
            gender:'',
            salary:'',
            profileUrl:'',
            startDate:''

        }

        if(formValue.name.length<1)
        {
            error.name='name is required field'
            isError=true;
        }

        if(formValue.gender.length<1)
        {
            error.gender='gender is required field'
            isError=true;
        }

        if(formValue.salary.length<1)
        {
            error.salary='salary is required field'
            isError=true;
        }
        if( parseInt(formValue.salary) <30000 || parseInt(formValue.salary) >50000)
        {
            error.salary='salary should be between 30,000 and 50,000'
            isError=true;
        }

        if(formValue.profileUrl.length<1)
        {
            error.profileUrl='profile is required field'
            isError=true;
        }

        if(formValue.departMentValue.length<1)
        {
            error.departMentValue='department is required field'
            isError=true;
        }

        await setForm({...formValue,error:error})
        return isError;
    }

    const save = async(event)=>{
        event.preventDefault();
        console.log("save");

        if(await validData())
        {
            console.log('error',formValue);
            return;
        }

        let object ={

            name:formValue.name,
            departMentValue:formValue.departMentValue,
            gender:formValue.gender,
            salary:formValue.salary,
            startDate:`${formValue.day} ${formValue.month} ${formValue.year}`,
            notes:formValue.notes,
            id:formValue.id,
            profileUrl:formValue.profileUrl,
        }

        employeeService.addEmployee(object).then(data=>{
            console.log("data added");
            setDisplayeMessage("Successfullly added User")
            setTimeout(()=>{
                window.location.reload();},3000);

        })
        .catch(err =>{
            console.log("err while add");
            setDisplayeMessage("Error while  adding")

        })



    }


    const reset=()=>{
        setForm({...initialValue,id:formValue.id,isUpdate: formValue.isUpdate})
        console.log(formValue)
    }

    return(

        <div className="payroll-main">
            <PageHeader/>
            <div className="content">
                <form className="form" action="#" onSubmit={save}>
                    <div className="form-head">Employee payroll form</div>
                    <div className="row">
                        <label className="label text" htmlFor="name">Name</label>
                        <input className="input" type="text" id="name" name="name" value={formValue.name} onChange={changeValue} placeholder="Your name.."/>

                    </div>
                    <div className="error">{formValue.error.name}</div>
                    <div className="row">
                        <label className="label text" htmlFor="profileUrl">Profile image</label>
                        <div className="profile-radio-button">
                            <label>
                                <input type='radio' checked={formValue.profileUrl=='profile-images/Ellipse -3.png'} name="profileUrl"
                                value="profile-images/Ellipse -3.png" onChange={changeValue}/>
                                <img className="profile" src={profile1}/>
                            </label>
                            <label>
                                <input type='radio' checked={formValue.profileUrl=='profile-images/Ellipse -2.png'} name="profileUrl"
                                value="profile-images/Ellipse -2.png" onChange={changeValue}/>
                                <img className="profile" src={profile2}/>
                            </label>
                            <label>
                                <input type='radio' checked={formValue.profileUrl=='profile-images/Ellipse -1.png'} name="profileUrl"
                                value="profile-images/Ellipse -1.png" onChange={changeValue}/>
                                <img className="profile" src={profile3}/>
                            </label>
                            <label>
                                <input type='radio' checked={formValue.profileUrl=='profile-images/Ellipse -8.png'} name="profileUrl"
                                value="profile-images/Ellipse -8.png" onChange={changeValue}/>
                                <img className="profile" src={profile4}/>
                            </label>
                        </div>
                    </div>
                    <div className="error">{formValue.error.profileUrl}</div>
                    <div className="row">
                        <label className="label text" htmlFor="gender">Gender</label>
                        <div>
                            <input type="radio" id="male" onChange={changeValue} name="gender" value="male"/>
                            <label className="text" htmlFor="male">Male</label>
                            <input type="radio" id="female" onChange={changeValue} name="gender" value="female"/>
                            <label className="text" htmlFor="female">Female</label>

                        </div>
                    </div>
                    <div className="error">{formValue.error.gender}</div>
                    <div className="row">
                        <label className="label text" htmlFor="department">Department</label>
                        <div>
                            {formValue.allDepartment.map(item=>(
                                <span className="deptlabel" key={item}>

                                    <input className= "checkbox " type="checkbox" onChange={()=> onCheckChange(item)} name={item}
                                    defaultChecked={()=>getChecked(item)} value={item}/>

                                    <label className="text " htmlFor={item}>{item}</label>
                                </span>
                            ))}
                        </div>

                    </div>

                    <div className="error">{formValue.error.department}</div>
                    <div className="row">
                        <label className="label text" htmlFor="salary">Salary</label>
                        <input className="input" type="number" onChange={changeValue} id="salary" value={formValue.salary} name="salary" placeholder="Salary"/>

                    </div>
                    <div className="error">{formValue.error.salary}</div>
                    <div className="row"><label className="label text" htmlFor="startDate">Start date</label><div>
                        <select onChange={changeValue} id="day" name="day">
                            <option value="" disabled selected>Day</option>
                            <option value="01">1</option>
                            <option value="02">2</option>
                            <option value="03">3</option>
                            <option value="04">4</option>
                            <option value="05">5</option>
                            <option value="06">6</option>
                            <option value="07">7</option>
                            <option value="08">8</option>
                            <option value="09">9</option>
                            <option value="10">10</option>
                            <option value="11">11</option>
                            <option value="12">12</option>
                            <option value="13">13</option>
                            <option value="14">14</option>
                            <option value="15">15</option>
                            <option value="16">16</option>
                            <option value="17">17</option>
                            <option value="18">18</option>
                            <option value="19">19</option>
                            <option value="20">20</option>
                            <option value="21">21</option>
                            <option value="22">22</option>
                            <option value="23">23</option>
                            <option value="24">24</option>
                            <option value="25">25</option>
                            <option value="26">26</option>
                            <option value="27">27</option>
                            <option value="28">28</option>
                            <option value="29">29</option>
                            <option value="30">30</option>
                            <option value="31">31</option>
                        </select>
                        <select onChange={changeValue} id="month" name="month">
                            <option value="" disabled selected>Month</option>
                            <option value="01">January</option>
                            <option value="02">February</option>
                            <option value="03">March</option>
                            <option value="04">April</option>
                            <option value="05">May</option>
                            <option value="06">June</option>
                            <option value="07">July</option>
                            <option value="08">August</option>
                            <option value="09">September</option>
                            <option value="10">October</option>
                            <option value="11">November</option>
                            <option value="12">December</option>
                        </select>
                        <select onChange={changeValue} id="year" name="year">
                            <option value="" disabled selected>Year</option>
                            <option value="2021">2021</option>
                            <option value="2020">2020</option>
                            <option value="2019">2019</option>
                            <option value="2018">2018</option>
                            <option value="2017">2017</option>
                            <option value="2016">2016</option>
                        </select>
                        </div>

                    </div>
                    <div className="error">{formValue.error.startDate}</div>
                    <div className="row">
                        <label className="label text" htmlFor="notes">Notes</label>
                        <textarea onChange={changeValue} id="notes" value={formValue.notes} className="input" name="notes" placeholder=""
                            style={{heigt:'100%'}}></textarea>

                    </div>

                    <div className="buttonParent">
                        <a routerLink="" className="resetButton button cancelButton">Cancel</a>
                        <div className="submit-reset">
                            <button type="submit" className="button submitButton" id="submitButton">{formValue.isUpdate?'Update':'Submit'}</button>
                            <button type="button" onClick={reset} className="resetButton button">Reset</button>
                        </div>
                    </div>

                    <div className="displayMessage">{displayMessage}</div>


                </form>
            </div>
        </div>


    )
}

export default PayrollForm
